import 'package:flutter/material.dart'; 
import '../screens/product_detail_screen.dart';

class RelatedProducts extends StatelessWidget { final List relatedProducts;

const RelatedProducts({super.key, required this.relatedProducts});

@override Widget build(BuildContext context) { return Column( crossAxisAlignment: CrossAxisAlignment.start, children: [ const Padding( padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8), child: Text( 'Related Products', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold), ), ), SizedBox( height: 200, child: ListView.builder( scrollDirection: Axis.horizontal, padding: const EdgeInsets.symmetric(horizontal: 16), itemCount: relatedProducts.length, itemBuilder: (context, index) { final product = relatedProducts[index]; return GestureDetector( onTap: () { Navigator.push( context, PageRouteBuilder( pageBuilder: (context, animation, secondaryAnimation) => ProductDetailScreen(product: product), transitionsBuilder: (context, animation, secondaryAnimation, child) { const begin = Offset(1.0, 0.0); const end = Offset.zero; const curve = Curves.easeInOut; var tween = Tween(begin: begin, end: end).chain(CurveTween(curve: curve)); return SlideTransition(position: animation.drive(tween), child: child); }, ), ); }, child: Container( width: 150, margin: const EdgeInsets.only(right: 16), child: Card( child: Column( crossAxisAlignment: CrossAxisAlignment.start, children: [ Hero( tag: 'product-${product.id}', child: Image.asset( product.imageUrl, height: 100, width: double.infinity, fit: BoxFit.cover, ), ), Padding( padding: const EdgeInsets.all(8.0), child: Column( crossAxisAlignment: CrossAxisAlignment.start, children: [ Text( product.name, style: const TextStyle(fontSize: 16, fontWeight: FontWeight.w600), maxLines: 1, overflow: TextOverflow.ellipsis, ), Text( 'Rs. ${product.price.toStringAsFixed(2)}', style: const TextStyle(color: Colors.deepPurple), ), ], ), ), ], ), ), ), ); }, ), ), ], ); } }